import os
import time

dateTime = system.date.format(system.date.now(),"yyyy-MM-dd-HHmmss")	
backupTypes = self.parent.parent.getChild("OptionsContainer").getChild("FlexRepeater").props.instances
installationPath = settings.getValue('Global', 'installationPathIA')

projectName = self.parent.parent.getChild("Header").getChild("SelectProject").props.params.value

if projectName:
	dirName = projectName + '_' + dateTime
else:
	dirName = 'Backup' + '_' + dateTime 

resourcesPath = settings.getValue('Global', 'serverTempSaveLocation') + '/' + dirName + '/resources'

if not os.path.exists(resourcesPath):
	os.makedirs(resourcesPath)


for backupType in backupTypes: 

	# ------------ Tags -------------------------------------------		
	if backupType['type'] == 'tags':

		options = backupType['options']

		for option in options:
			if option['value']:
				tagProvider = option['label'].split(']')[0][1:]
				tagLabel = option['label'][len(tagProvider)+2:].replace('/','-')
				fileName = tagProvider + '__' + tagLabel +   ".json"
				system.tag.exportTags(resourcesPath + '/tags/' + fileName, [option['label']], True)




	# ------------ Database Tables -------------------------------------------		
	if backupType['type'] == 'tables':


		options = backupType['options']

		for option in options:
			if option['value']:

				fullName = option['label']
				tableName = fullName.split(']')[-1]
				dbName = fullName.split(']')[0][1:]

				fileName = tableName +  ".csv"

				databaseTables = system.tag.readBlocking(self.session.custom.tagProvider + "RapidDev/Backup Restore/Settings/databaseTables")[0].value
				for row in range(databaseTables.getRowCount()):
					if tableName == databaseTables.getValueAt(row,'Table') and dbName == databaseTables.getValueAt(row,'Database'):
						dbType = databaseTables.getValueAt(row,'Translator')
						csvDataset = dbeditor.util.exportDBtable(dbType, dbName, tableName, download=False)
						system.file.writeFile(resourcesPath + '/tables/' + dbName + '__' + fileName, csvDataset)




	# ------------ Themes --------------------------------------------------------
	if backupType['type'] == 'themes':		

		themesPath = installationPath + '/Ignition/data/modules/com.inductiveautomation.perspective/themes'

		options = backupType['options']
		for option in options:
			if option['value']:
				fileName = option['label'] + '.css'
				folderName = option['label']
				backup.util.copyFile(themesPath + '/' + fileName, resourcesPath + '/themes/' + fileName)
				backup.util.copyFolder(themesPath + '/' + folderName, resourcesPath + '/themes/' + folderName)



	# ------------ Icons --------------------------------------------------------
	if backupType['type'] == 'icons':		

		iconsPath = installationPath + '/Ignition/data/modules/com.inductiveautomation.perspective/icons'

		options = backupType['options']
		for option in options:
			if option['value']:
				fileName = option['label'] + '.svg'		
				backup.util.copyFile(iconsPath + '/' + fileName, resourcesPath + '/icons/' + fileName)




backup.util.zipTempDirectory(dirName)
backup.util.downloadTempFile(dirName + '.zip')


