onChange.script:

projectName = currentValue.value

resourceTypes = ['tags','tables','themes','icons']


def convertName(name):
	if resourceType == 'tags':
		name = '[' + name.replace('__',']')
		name = name.split('.')[0]

	elif resourceType == 'tables':
		name = '[' + name.replace('__',']')
		if '.csv' in name:
			name = name[:-len('.csv')]

	elif resourceType == 'themes':
		if '.css' in name:
			name = name[:-len('.css')]

	elif resourceType == 'icons':
		name = name.split('.')[0]

	return name

instances = []
for resourceType in resourceTypes:
	fileNames = backup.util.getResourceNamesFromProject(resourceType, projectName)

	options = []
	for fileName in fileNames:
		options.append({'label':convertName(fileName), 'value':False, 'fileName': fileName, 'viewPath': 'Backup Restore/Components/Checkbox Option'})

	instances.append({'type': resourceType, 'options': options})


self.parent.parent.getChild("OptionsContainer").getChild("FlexRepeater").props.instances = instances

