import os

dateTime = system.date.format(system.date.now(),"yyyy-MM-dd-HHmmss")	
backupTypes = self.parent.parent.getChild("OptionsContainer").getChild("FlexRepeater").props.instances
installationPath = settings.getValue('Global', 'installationPathIA')
tempSaveLocation = settings.getValue('Global', 'serverTempSaveLocation')

if not os.path.exists(tempSaveLocation):
	os.makedirs(tempSaveLocation)

for backupType in backupTypes: 

	# ------------ Tags -------------------------------------------		
	if backupType['type'] == 'Tags':

		options = backupType['options']

		for option in options:
			if option['value']:
				tagProvider = option['label'].split(']')[0][1:]
				tagLabel = option['label'][len(tagProvider)+2:].replace('/','-')
				fileName = "TagExport_" + tagProvider + '__' + tagLabel + '_' + dateTime +  ".json"
				backup.util.exportTags(fileName, option['label'])
				backup.util.downloadTempFile(fileName)



	# ------------ Database Tables -------------------------------------------		
	if backupType['type'] == 'Database Tables':


		options = backupType['options']

		for option in options:
			if option['value']:
				tableName = option['label']

				fileName = "TableExport_" + tableName + "_" + dateTime +  ".csv"

				databaseTables = system.tag.readBlocking(self.session.custom.tagProvider + "RapidDev/Backup Restore/Settings/databaseTables")[0].value
				for row in range(databaseTables.getRowCount()):
					if tableName == databaseTables.getValueAt(row,'Table'):
						dbName = databaseTables.getValueAt(row,'Database')
						dbType = databaseTables.getValueAt(row,'Translator')

						dbeditor.util.exportDBtable(dbType, dbName, tableName, fileName)


	# ------------ Themes --------------------------------------------------------
	if backupType['type'] == 'Themes':		
		tempDirectory = 'Themes_Backup_' + dateTime
		themesPath = installationPath + '/Ignition/data/modules/com.inductiveautomation.perspective/themes'
		options = backupType['options']
		empty = True
		for option in options:
			if option['value']:
				backup.util.copyToTempDirectory(themesPath + '/' + option['label'], tempDirectory + '/' + option['label'])
				empty = False

		if not empty:
			backup.util.zipTempDirectory(tempDirectory)
			backup.util.downloadTempFile(tempDirectory + '.zip')



	# ------------ Icons --------------------------------------------------------
	if backupType['type'] == 'Icons':		
		tempDirectory = 'Icons_Backup_' + dateTime
		iconsPath = installationPath + '/Ignition/data/modules/com.inductiveautomation.perspective/icons'
		options = backupType['options']
		empty = True
		for option in options:
			if option['value']:

				if not os.path.exists(tempSaveLocation + '/' + tempDirectory):
					os.makedirs(tempSaveLocation + '/' + tempDirectory)
				backup.util.copyToTempDirectory(iconsPath + '/' + option['label'] + '.svg', tempDirectory + '/' + option['label'] + '.svg')
				empty = False

		if not empty:
			backup.util.zipTempDirectory(tempDirectory)
			backup.util.downloadTempFile(tempDirectory + '.zip')


import os

restoreTypes = self.parent.parent.getChild("OptionsContainer").getChild("FlexRepeater").props.instances
projectName = self.parent.parent.getChild("Header").getChild("SelectProject").props.params.value
tempSaveLocation = settings.getValue('Global', 'serverTempSaveLocation')

installationPath = settings.getValue('Global','installationPathIA')
resourcesPath = installationPath + '/Ignition/data/projects/' + projectName + '/resources'

for restoreType in restoreTypes: 

	# ------------ Tags -------------------------------------------		
	if restoreType['type'] == 'tags':


		options = restoreType['options']

		for option in options:
			if option['value']:
				tagProvider = option['label'].split(']')[0][1:]
				tagPath = option['label'].split(']')[-1].replace('-','/')
				if '/' in tagPath:
					parentPath = '/'.join(tagPath.split('/')[:-1])
				else:
					parentPath = ''

				system.tag.importTags(resourcesPath + '/tags/' + option['fileName'], '[' + tagProvider + ']' + parentPath)





	# ------------ Database Tables -------------------------------------------		
	if restoreType['type'] == 'tables':


		options = restoreType['options']

		for option in options:
			if option['value']:
				fullName = option['label']
				tableName = fullName.split(']')[-1]
				dbName = fullName.split(']')[0][1:]

				databaseTables = system.tag.readBlocking(self.session.custom.tagProvider + "RapidDev/Backup Restore/Settings/databaseTables")[0].value


				for row in range(databaseTables.getRowCount()):
					if tableName == databaseTables.getValueAt(row,'Table') and dbName == databaseTables.getValueAt(row,'Database'):
						dbType = databaseTables.getValueAt(row,'Translator')

						try:
							queryPath = 'General/' + dbType + '/getPrimaryKey'
							primaryKey = system.db.runNamedQuery(queryPath, {'database': dbName, 
																						'tableName':tableName.split('.')[-1], 
																						'tableSchema': tableName.split('.')[0] }).getValueAt(0,0)

							data_string = system.file.readFileAsString(resourcesPath + '/tables/' + option['fileName'])
							dataset = system.dataset.fromCSV(data_string)
							dbeditor.util.importDSintoDBtable(dataset, dbName, tableName, primaryKey, deleteExtra=True)
						except:
							print 'Failed to restore table: ', tableName




	# ------------ Themes --------------------------------------------------------
	if restoreType['type'] == 'themes':		

		themesPath = installationPath + '/Ignition/data/modules/com.inductiveautomation.perspective/themes'

		options = restoreType['options']
		for option in options:
			if option['value']:
				fileName = option['label'] + '.css'
				folderName = option['label']
				backup.util.copyFile(resourcesPath + '/themes/' + fileName, themesPath + '/' + fileName)
				backup.util.copyFolder(resourcesPath + '/themes/' + folderName, themesPath + '/' + folderName)




	# ------------ Icons --------------------------------------------------------				
	if restoreType['type'] == 'icons':		

		iconsPath = installationPath + '/Ignition/data/modules/com.inductiveautomation.perspective/icons'

		options = restoreType['options']

		for option in options:
			if option['value']:
				print 'copying from ', resourcesPath + '/icons/' + option['fileName']
				print 'to ', iconsPath + '/' + option['fileName']
				backup.util.copyFile(resourcesPath + '/icons/' + option['fileName'],  iconsPath + '/' + option['fileName'])

